root.template:
	//! Indicates interrupt handler has been installed 	static constexpr bool irqHandlerInstalled = $(IRQ_HANDLER);


	//! Default IRQ level
 	static constexpr uint32_t irqLevel = $(IRQ_LEVEL);


	//! Frequency of RTC External Clock or Crystal
 	static constexpr uint32_t rtcclk_clock = $(rtcclk_clock)UL;


	//! Oscillator control register
 	static constexpr uint32_t RTC_CR = 	 RTC_CR_OSCE($(rtc_cr_osce)) | // Enables 32kHz oscillator [RTC_32K] 	 RTC_CR_CLKO($(rtc_cr_clko)) | // Enables RTC 32kHz Clock Output 	 RTC_CR_UM($(rtc_cr_um)) | // Update Mode 	 RTC_CR_SUP($(rtc_cr_sup)) | // Supervisor access 	 RTC_CR_WPE($(rtc_cr_wpe)) | // Wakeup Pin Enable 	 ($(rtc_cr_scp)<<RTC_CR_SC16P_SHIFT); // RTC Oscillator load capacitance
