root.template:
	//! Indicates interrupt handler has been installed 	static constexpr bool irqHandlerInstalled = $(IRQ_HANDLER);

 	//! Default IRQ level
 	static constexpr uint32_t irqLevel = $(IRQ_LEVEL);

 	//! Oscillator control register
 	static constexpr uint32_t RTC_CR = 	 ($(CR_OSCE)<<RTC_CR_OSCE_SHIFT)| 	 ($(CR_CLKO)<<RTC_CR_CLKO_SHIFT)| 	 ($(CR_UM)<<RTC_CR_UM_SHIFT)| 	 ($(CR_SUP)<<RTC_CR_SUP_SHIFT)| 	 ($(CR_WPE)<<RTC_CR_WPE_SHIFT)| 	 ($(CR_SCP)<<RTC_CR_SC16P_SHIFT);
