root.template:
	//! Indicates interrupt handler has been installed 	static constexpr bool irqHandlerInstalled = $(IRQ_HANDLER);

 	//! Default value for ADCx_CFG1 register 	static constexpr uint32_t CFG1 = 	 ADC_CFG1_ADICLK($(cfg1_adiclk))| 	 ADC_CFG1_MODE($(cfg1_mode))| 	 ADC_CFG1_ADLSMP($(cfg1_adlsmp))| 	 ADC_CFG1_ADIV($(cfg1_adiv))| 	 ADC_CFG1_ADLPC($(cfg1_adlpc));

 	//! Default value for ADCx_CFG2 register 	 static constexpr uint32_t CFG2 = 	 ADC_CFG2_MUXSEL_MASK | // Choose 'b' channels 	 ADC_CFG2_ADLSTS($(cfg2_adlsts))| 	 ADC_CFG2_ADHSC($(cfg2_adhsc))| 	 ADC_CFG2_ADACKEN($(cfg2_adacken));

 	static constexpr uint32_t SC2 = 	 ADC_SC2_REFSEL($(sc2_refsel))| 	 ADC_SC2_DMAEN($(sc2_dmaen))| 	 ADC_SC2_ACREN($(sc2_compare))| 	 ADC_SC2_ADTRG($(sc2_adtrg));

 	//! Default IRQ level
 	static constexpr uint32_t irqLevel = $(IRQ_LEVEL);
