root.template:
	//! Indicates interrupt handler has been installed 	static constexpr bool irqHandlerInstalled = $(IRQ_HANDLER);


	//! Default IRQ level
 	static constexpr uint32_t irqLevel = $(IRQ_LEVEL);


	//! Frequency of RTC External Clock or Crystal
 	static constexpr uint32_t rtcclk_clock = $(rtcclk_clock)UL;


	//! CMP Control Register 0
 	static constexpr uint32_t CMP_CR0 = 	 CMP_CR0_FILTER_CNT($(cmp_cr0_filter_cnt)) | // Filter Sample Count 	 CMP_CR0_FILTER_HYSTCTR($(cmp_cr0_hystctr)); // Comparator hard block hysteresis control


	//! CMP Control Register 1
 	static constexpr uint32_t CMP_CR1 = 	 CMP_CR1_SE($(cmp_cr1_se)) | // Sample Enable 	 CMP_CR1_WE($(cmp_cr1_we)) | // Windowing Enable 	 CMP_CR1_TRIGM($(cmp_cr1_trigm)) | // Trigger Mode Enable 	 CMP_CR1_PMODE($(cmp_cr1_pmode)) | // Power mode Select 	 CMP_CR1_INV($(cmp_cr1_inv)) | // Comparator Invert 	 CMP_CR1_COS($(cmp_cr1_cos)) | // Comparator Output Select 	 CMP_CR1_OPE($(cmp_cr1_ope)) | // Comparator Output Pin Enable 	 CMP_CR1_EN($(cmp_cr1_en)); // Comparator Module Enable


	//! CMP Filter Period Register
 	static constexpr uint32_t CMP_FPR = 	 CMP_FPR_FILT_PER($(cmp_fpr_filt_per)); // Filter Sample Period
