devicePage.template:
/** Selects EEPROM size */ 	enum EepromSel { 	 eeprom32Bytes, 	 eeprom64Bytes, 	 eeprom128Bytes, 	 eeprom256Bytes, 	 eeprom512Bytes, 	 eeprom1KBytes, 	 eeprom2KBytes, 	};


/** Selects division of FlexNVM between flash and EEPROM backing storage */ 	enum PartitionSel { 	 partition_flash32K_eeprom0K, 	 partition_flash24K_eeprom8K, 	 partition_flash16K_eeprom16K, 	 partition_flash8K_eeprom24K, 	 partition_flash0K_eeprom32K, 	 	 // All EEPROM 	 partition_flash0K_eeprom_all = partition_flash0K_eeprom32K, 	};


/** 	 * Selects division of the EEPROM 	 * Not supported on this device 	 */ 	enum PartitionSplit { 	 // Not supported 	 partition_disabled=0x30, 	};


//! FlexNVM - EEPROM size 	static constexpr EepromSel eepromSel = $(EepromSize);


//! FlexNVM - Flash EEPROM partition 	static constexpr PartitionSel partitionSel = $(FlashPartition);


//! FlexNVM - EEPROM partition - not available 	static constexpr PartitionSplit partitionSplit = partition_disabled;
