devicePage.template:
// Sector size for program flash (minimum erase element) 	static constexpr unsigned programFlashSectorSize = $(pflash_sector_size);


// Phrase size for program flash (minimum programming element) 	static constexpr unsigned programFlashPhraseSize = $(pflash_phrase_size);


// Sector size for data flash (minimum erase element) 	static constexpr unsigned dataFlashSectorSize = $(dflash_sector_size);


// Phrase size for data flash (minimum programming element) 	static constexpr unsigned dataFlashPhraseSize = $(dflash_phrase_size);


struct EepromSizes { 	 const uint16_t size; // EEPROM size 	 const uint8_t value; // Value to select size 	};


/** EEPROM Data Set Size Field */ 	static constexpr EepromSizes eepromSizes[] = { 	 // Size Value 	 { 32, 0x09, }, 	 { 64, 0x08, }, 	 { 128, 0x07, }, 	 { 256, 0x06, }, 	 { 512, 0x05, }, 	 { 1024, 0x04, }, 	 { 2048, 0x03, }, 	};


struct PartitionInformation { 	 const uint32_t flashSize; //! Remaining data flash 	 const uint32_t eeepromSize; //! Flash allocated to EEPROM backing store 	 const uint8_t value; //! Partition value 	};


/** Flash partition information */ 	static constexpr PartitionInformation partitionInformation[] { 	 // Flash Backing Value 	 { 32*1024, 0*1024 , 0xFF}, 	 { 24*1024, 8*1024 , 0x01}, 	 { 16*1024, 16*1024, 0x0A}, 	 { 8*1024, 24*1024, 0x09}, 	 { 0*1024, 32*1024, 0x08}, 	};


/** Selects EEPROM size */ 	enum EepromSel { 	 eeprom32Bytes, 	 eeprom64Bytes, 	 eeprom128Bytes, 	 eeprom256Bytes, 	 eeprom512Bytes, 	 eeprom1KBytes, 	 eeprom2KBytes, 	};


/** Selects division of FlexNVM between flash and EEPROM backing storage */ 	enum PartitionSel { 	 partition_flash32K_eeprom0K, 	 partition_flash24K_eeprom8K, 	 partition_flash16K_eeprom16K, 	 partition_flash8K_eeprom24K, 	 partition_flash0K_eeprom32K, 	 	 // All EEPROM 	 partition_flash0K_eeprom_all = partition_flash0K_eeprom32K, 	};


/** 	 * Selects division of the EEPROM 	 * Not supported on this device 	 */ 	enum PartitionSplit { 	 // Not supported 	 partition_disabled=0x30, 	};


//! FlexNVM - EEPROM size 	static constexpr EepromSel eepromSel = $(EepromSize);


//! FlexNVM - Flash EEPROM partition 	static constexpr PartitionSel partitionSel = $(FlashPartition);


//! FlexNVM - EEPROM partition - not available 	static constexpr PartitionSplit partitionSplit = partition_disabled;
