devicePage.template:
	//! Timer external input frequency 
 	static constexpr uint32_t ftmExternalClock = $(ftmExternalClock);


	//! Default Timer Period
 	static constexpr uint32_t period = $(period);


	//! Default value for SC register
 	static constexpr uint32_t sc = 	 FTM_SC_TOIE($(irqEnabled))| // Timer Overflow Interrupt Enable 	 FTM_SC_CPWMS($(ftm_sc_cpwms))| // Centre-Aligned PWM Select 	 FTM_SC_CLKS($(ftm_sc_clks))| // Clock Mode Selection 	 FTM_SC_TOIE($(ftm_sc_toie))| // Timer Overflow Interrupt Enable 	 FTM_SC_PS($(ftm_sc_ps)); // Prescale Factor Selection

\t/**
\t * Get input clock frequency
\t *
\t * @return Input clock frequency as an uint32_t in Hz
\t */
\tstatic uint32_t getInputClockFrequency() {
\t   /** Pointer to hardware */
\t   static constexpr volatile FTM_Type *ftm = reinterpret_cast<volatile FTM_Type *>(basePtr);
\t
\t   switch(ftm->SC&FTM_SC_CLKS_MASK) {
\t   default:
\t   case FTM_SC_CLKS(0): return 0;
\t   case FTM_SC_CLKS(1): return SystemBusClock;
\t   case FTM_SC_CLKS(2): return SystemMcgffClock;
\t   case FTM_SC_CLKS(3): return ftmExternalClock;
\t   }
\t}\n\n

\t/**
\t * Get clock frequency
\t *
\t * @return Frequency as a float in Hz
\t */
\tstatic float getClockFrequencyF() {
\t   /** Pointer to hardware */
\t   static constexpr volatile FTM_Type *ftm = reinterpret_cast<volatile FTM_Type *>(basePtr);
\t
\t   float freq = getInputClockFrequency();
\t   return freq/(1<<((ftm->SC&FTM_SC_PS_MASK)>>FTM_SC_PS_SHIFT));
\t}\n\n

\t/**
\t * Get clock frequency
\t *
\t * @return Frequency as a uint32_t in Hz (may underflow)
\t */
\tstatic uint32_t getClockFrequency() {
\t   /** Pointer to hardware */
\t   static constexpr volatile FTM_Type *ftm = reinterpret_cast<volatile FTM_Type *>(basePtr);
\t
\t   uint32_t freq = getInputClockFrequency();
\t   return freq/(1<<((ftm->SC&FTM_SC_PS_MASK)>>FTM_SC_PS_SHIFT));
\t}\n\n
