devicePage.template:
	//! Default PSR value
 	static constexpr uint32_t psr = 	 LPTMR_PSR_PRESCALE($(lptmr_psr_prescaler))| 	 LPTMR_PSR_PBYP($(lptmr_psr_pbyp))| 	 LPTMR_PSR_PCS($(lptmr_psr_pcs));


	//! Default CSR value
 	static constexpr uint32_t csr = 	 LPTMR_CSR_TMS($(lptmr_csr_tms))| 	 LPTMR_CSR_TFC($(lptmr_csr_tfc))| 	 LPTMR_CSR_TPP($(lptmr_csr_tpp))| 	 LPTMR_CSR_TPS($(lptmr_csr_tps));


	//! Default Timer period
 	static constexpr uint32_t period = $(period);
