devicePage.template:
\t//! Indicates need for special handling of CLKDIV1 register
\tstatic constexpr int ERRATA_E2448 = $(ERRATA_E2448:0);\n\n

\tenum ClockMode {
\t   ClockMode_None     = -1,
\t   ClockMode_LIRC_8M  = 0,
\t   ClockMode_LIRC_2M,
\t   ClockMode_HIRC_48M,
\t   ClockMode_EXT,
\t};\n\n

\t//! Frequency of Slow Internal Reference Clock [~2MHz]
\tstatic constexpr uint32_t system_slow_irc_clock = $(system_slow_irc_clock)UL;\n\n

\t//! Frequency of Fast Internal Reference Clock [~8MHz]
\tstatic constexpr uint32_t system_fast_irc_clock = $(system_fast_irc_clock)UL;\n\n

\tstruct ClockInfo {
\t   //! Clock Mode
\t   const ClockMode clockMode:8;
\t   //! Control Register 1
\t   const uint8_t c1;
\t   //! Control Register 2
\t   const uint8_t c2;
\t   //! Status and Control Register
\t   const uint8_t sc;
\t   //! Miscellaneous Control Register
\t   const uint8_t mc;
\t};\n\n

\tstatic const ClockInfo clockInfo[];

\t/**
\t * Get MCGERCLK
\t *
\t * @return MCGERCLK as uint32_t
\t */
\tstatic uint32_t getErcClock() {
\t   return Osc0Info::getInternalClock();
\t}\n\n

\t/**
\t * Get Internal MCGIRCLK (ungated by MCG_C1_IRCLKEN_MASK)
\t *
\t * @return MCGIRCLK as uint32_t
\t */
\tstatic uint32_t getInternalIrcClock() {
\t      if (mcg->C2&MCG_C2_IRCS_MASK) {
\t#ifdef MCG_SC_FCRDIV_MASK
\t         return (system_fast_irc_clock/(1<<((mcg->SC&MCG_SC_FCRDIV_MASK)>>MCG_SC_FCRDIV_SHIFT)));
\t#else
\t         return system_fast_irc_clock;
\t#endif
\t      }
\t      else {
\t         return system_slow_irc_clock;
\t      }
\t}\n\n

\t/**
\t * Get MCGIRCLK (gated by MCG_C1_IRCLKEN_MASK)
\t *
\t * @return MCGIRCLK as uint32_t
\t */
\tstatic uint32_t getIrcClock() {
\t   if (mcg->C1&MCG_C1_IRCLKEN_MASK) {
\t      return getInternalIrcClock();
\t   }
\t   else {
\t      return 0;
\t   }
\t}\n\n

const McgInfo::ClockInfo McgInfo::clockInfo[1] = { 	 { 	 //! Clock Mode 	 $(clock_mode),


 //! Control Register 1 	 MCG_C1_IRCLKEN($(mcg_c1_irclken)) | // IRCLEN Internal Reference Clock Enable 	 MCG_C1_IREFSTEN($(mcg_c1_irefsten)), // IREFSTEN Internal Reference Stop Enable


 //! Control Register 2 	 MCG_C2_RANGE0($(/OSC0/range)) | // RANGE Frequency Range Select 	 MCG_C2_HGO0($(/OSC0/hgo0)) | // HGO High Gain Oscillator Select 	 MCG_C2_EREFS0($(/OSC0/erefs0)) | // EREFS External Reference Select 	 MCG_C2_IRCS($(mcg_c2_ircs)), // IRCS Internal Reference Clock Select


 //! Status and Control Register 	 MCG_SC_FCRDIV($(mcg_sc_fcrdiv)), // FCRDIV Internal Clock Reference Divider


 //! Control Register 1 	 MCG_MC_HIRCEN($(mcg_mc_hircen)) | // HIRCEN Internal Reference Clock Enable 	 MCG_MC_LIRC_DIV2($(mcg_mc_lirc_div2)), // LIRC_DIV2 Internal Reference Stop Enable


 } 	};
