root.template:
	//! Indicates interrupt handler has been installed 	static constexpr bool irqHandlerInstalled = $(IRQ_HANDLER);


	//! Default IRQ level
 	static constexpr uint32_t irqLevel = $(IRQ_LEVEL);


	enum ClockMode { 	 ClockMode_None = -1, 	 ClockMode_FEI = 0, 	 ClockMode_FEE, 	 ClockMode_FBI, 	 ClockMode_BLPI, 	 ClockMode_FBE, 	 ClockMode_BLPE, 	 ClockMode_PBE, 	 ClockMode_PEE, 	};


	//! Clock Mode 	static constexpr ClockMode clockMode = $(clock_mode);


	//! Frequency of Frequency of Internal 48MHz Clock
 	static constexpr uint32_t irc48m_clock = $(system_irc48m_clock)UL;


	//! Frequency of Slow Internal Reference Clock [~32kHz]) 	static constexpr uint32_t system_slow_irc_clock = $(system_slow_irc_clock)UL;


	//! Frequency of Fast Internal Reference Clock [~4MHz]) 	static constexpr uint32_t system_fast_irc_clock = $(system_fast_irc_clock)UL;


	//! Control Register 1 	static constexpr uint32_t MCG_C1 = 	 MCG_C1_FRDIV($(mcg_c1_frdiv)) | // FRDIV FLL External Reference Divider 	 MCG_C1_IRCLKEN($(mcg_c1_irclken)) | // IRCLEN Internal Reference Clock Enable 	 MCG_C1_IREFSTEN($(mcg_c1_irefsten)); // IREFSTEN Internal Reference Stop Enable


	//! Control Register 2 	static constexpr uint32_t MCG_C2 = 	 MCG_C2_LOCRE0($(mcg_c2_locre0)) | // LOLRE0 Loss of Clock Reset Enable 	 MCG_C2_RANGE0($(/OSC0/range)) | // RANGE Frequency Range Select 	 MCG_C2_HGO0($(/OSC0/hgo0)) | // HGO High Gain Oscillator Select 	 MCG_C2_EREFS0($(/OSC0/erefs0)) | // EREFS External Reference Select 	 MCG_C2_IRCS($(mcg_c2_ircs)); // IRCS Internal Reference Clock Select


	//! Control Register 4 	static constexpr uint32_t MCG_C4 = 	 MCG_C4_DMX32($(mcg_c4_dmx32)) | // DMX32 DCO lock range 	 MCG_C4_DRST_DRS($(mcg_c4_drst_drs)); // DRST_DRS DCO Range Select


	//! Control Register 5 	static constexpr uint32_t MCG_C5 = 	 MCG_C5_PLLCLKEN0($(mcg_c5_pllclken)) | // PLLCLKEN0 PLL Clock Enable 	 MCG_C5_PLLSTEN0($(mcg_c5_pllsten)) | // PLLSTEN0 PLL Stop Enable 	 MCG_C5_PRDIV0($(mcg_c5_prdiv0)); // PRDIV0 PLL External Reference Divider


	//! Control Register 6 	static constexpr uint32_t MCG_C6 = 	 MCG_C6_LOLIE0($(mcg_c6_lolie0)) | // LOLIE0 Loss of Lock interrupt Enable 	 MCG_C6_CME0($(mcg_c6_cme0)) | // CME0 Clock Monitor Enable 	 MCG_C6_VDIV0($(mcg_c6_vdiv0)); // VDIV0 PLL VCO Divider


	//! Status and Control Register 	static constexpr uint32_t MCG_SC = 	 MCG_SC_FCRDIV($(mcg_sc_fcrdiv)); // FCRDIV Internal Clock Reference Divider


	//! Control Register 7 	static constexpr uint32_t MCG_C7 = 	 MCG_C7_OSCSEL($(mcg_c7_oscsel)); // OSCSEL MCG OSC Clock Select


	//! Control Register 8 	static constexpr uint32_t MCG_C8 = 	 MCG_C8_LOCRE1($(mcg_c8_locre1)) | // LOCRE1 RTC Loss of Clock Reset Enable 	 MCG_C8_LOLRE($(mcg_c8_lolre)) | // LOLRE PLL Loss of Lock Reset Enable 	 MCG_C8_CME1($(mcg_c8_cme1)); // CME1 Clock Monitor Enable 1


	//! Control Register 9
 	static constexpr uint32_t MCG_C9 = 	 MCG_C9_PLL_CME($(mcg_c9_pll_cme)) | // PLL_CME MCG External PLL Clock Monitor Enable 	 MCG_C9_PLL_LOCRE($(mcg_c9_pll_locre)); // PLL_LOCRE MCG External PLL Loss of Clock Reset Enable
	//! Control Register 11
 	static constexpr uint32_t MCG_C11 = 	 MCG_C11_PLLCS($(mcg_c11_pllcs)); // PLLCS PLL Clock Select
	//! Clock divider 	static constexpr uint32_t SIM_CLKDIV1 = 	 SIM_CLKDIV1_OUTDIV4($(sim_clkdiv1_outdiv4)) | // SIM_CLKDIV1_OUTDIV4 	#ifdef SIM_CLKDIV1_OUTDIV3 	 SIM_CLKDIV1_OUTDIV3($(sim_clkdiv1_outdiv3)) | // SIM_CLKDIV1_OUTDIV3 	#endif 	 SIM_CLKDIV1_OUTDIV2($(sim_clkdiv1_outdiv2)) | // SIM_CLKDIV1_OUTDIV2 	 SIM_CLKDIV1_OUTDIV1($(sim_clkdiv1_outdiv1)); // SIM_CLKDIV1_OUTDIV1


	//! SIM SOPT1 OSC32KSEL 	static constexpr uint32_t sim_sopt1_osc32ksel = 	 SIM_SOPT1_OSC32KSEL($(sim_sopt1_osc32ksel));


	//! SIM SOPT2_RTCCLKOUTSEL 	static constexpr uint32_t sim_sopt2_rtcclkoutsel = 	 SIM_SOPT2_RTCCLKOUTSEL$(sim_sopt2_rtcclkoutsel));


	//! SIM SIM_SOPT2_PLLFLLSEL 	static constexpr uint32_t sim_sopt2_pllfllsel = 	 SIM_SOPT2_PLLFLLSEL($(sim_sopt2_pllfllsel));
