devicePage.template:
\t//! Not present on these devices
\tstatic constexpr int ERRATA_E2448 = 0;\n\n

\tenum ClockMode {
\t   ClockMode_None = -1,
\t   ClockMode_FEI  = 0,
\t   ClockMode_FEE,
\t   ClockMode_FBI,
\t   ClockMode_BLPI,
\t   ClockMode_FBE,
\t   ClockMode_BLPE,
\t   ClockMode_PBE,
\t   ClockMode_PEE,
\t};\n\n

\t//! Frequency of Internal 48MHz Clock
\tstatic constexpr uint32_t irc48m_clock = $(system_irc48m_clock)UL;\n\n

\tenum ModeFlags {
\t   Mode_LP  = (1<<0),
\t};\n\n

\t//! Frequency of Slow Internal Reference Clock [~32kHz]
\tstatic constexpr uint32_t system_slow_irc_clock = $(system_slow_irc_clock)UL;\n\n

\t//! Frequency of Fast Internal Reference Clock [~4MHz]
\tstatic constexpr uint32_t system_fast_irc_clock = $(system_fast_irc_clock)UL;\n\n

\t//! PLL VDIV min value
\tstatic constexpr uint32_t pll_vdiv_min = 24;\n\n

\t//! PLL post divider
\tstatic constexpr uint32_t pll_post_divider = 1;\n\n

\tstruct ClockInfo {
\t   //! Clock Mode
\t   const ClockMode clockMode:8;
\t   //! Mode flags
\t   const uint8_t modeFlags;
\t   //! Control Register 1
\t   const uint8_t c1;
\t   //! Control Register 2
\t   const uint8_t c2;
\t   //! Control Register 4
\t   const uint8_t c4;
\t   //! Control Register 5
\t   const uint8_t c5;
\t   //! Control Register 6
\t   const uint8_t c6;
\t   //! Status and Control Register
\t   const uint8_t sc;
\t   //! Control Register 7
\t   const uint8_t c7;
\t   //! Control Register 8
\t   const uint8_t c8;
\t};\n\n

// Various clock configurations
\tstatic const ClockInfo clockInfo[$(McgInfoSize)];\n\n

\t/**
\t * Get MCGERCLK
\t *
\t * @return MCGERCLK as uint32_t
\t */
\tstatic uint32_t getErcClock() {
\t
\t   switch((mcg->C7&MCG_C7_OSCSEL_MASK)) {
\t      default               : return 0;
\t      case MCG_C7_OSCSEL(0) : return Osc0Info::getInternalClock();
\t      case MCG_C7_OSCSEL(2) : return McgInfo::irc48m_clock;
\t   }
\t}\n\n

\t/**
\t * Get Internal MCGIRCLK (ungated by MCG_C1_IRCLKEN_MASK)
\t *
\t * @return MCGIRCLK as uint32_t
\t */
\tstatic uint32_t getInternalIrcClock() {
\t      if (mcg->C2&MCG_C2_IRCS_MASK) {
\t#ifdef MCG_SC_FCRDIV_MASK
\t         return (system_fast_irc_clock/(1<<((mcg->SC&MCG_SC_FCRDIV_MASK)>>MCG_SC_FCRDIV_SHIFT)));
\t#else
\t         return system_fast_irc_clock;
\t#endif
\t      }
\t      else {
\t         return system_slow_irc_clock;
\t      }
\t}\n\n

\t/**
\t * Get MCGIRCLK (gated by MCG_C1_IRCLKEN_MASK)
\t *
\t * @return MCGIRCLK as uint32_t
\t */
\tstatic uint32_t getMcgIrClock() {
\t   if (mcg->C1&MCG_C1_IRCLKEN_MASK) {
\t      return getInternalIrcClock();
\t   }
\t   else {
\t      return 0;
\t   }
\t}\n\n

{ 	 //! Clock Mode 	 $(clock_mode),


//! Mode flags 	 $(mode_flags:0),


//! Control Register 1 	 MCG_C1_FRDIV($(mcg_c1_frdiv)) | // FRDIV FLL External Reference Divider 	 MCG_C1_IRCLKEN($(mcg_c1_irclken)) | // IRCLEN Internal Reference Clock Enable 	 MCG_C1_IREFSTEN($(mcg_c1_irefsten)), // IREFSTEN Internal Reference Stop Enable


//! Control Register 2 	 MCG_C2_LOCRE0($(mcg_c2_locre0)) | // LOLRE0 Loss of Clock Reset Enable 	 MCG_C2_RANGE0($(/OSC0/range)) | // RANGE Frequency Range Select 	 MCG_C2_HGO0($(/OSC0/hgo0)) | // HGO High Gain Oscillator Select 	 MCG_C2_EREFS0($(/OSC0/erefs0)) | // EREFS External Reference Select 	 MCG_C2_IRCS($(mcg_c2_ircs)), // IRCS Internal Reference Clock Select


//! Control Register 4 	 MCG_C4_DMX32($(mcg_c4_dmx32)) | // DMX32 DCO lock range 	 MCG_C4_DRST_DRS($(mcg_c4_drst_drs)), // DRST_DRS DCO Range Select


//! Control Register 5 	 MCG_C5_PLLCLKEN0($(mcg_c5_pllclken)) | // PLLCLKEN0 PLL Clock Enable 	 MCG_C5_PLLSTEN0($(mcg_c5_pllsten)) | // PLLSTEN0 PLL Stop Enable 	 MCG_C5_PRDIV0($(mcg_c5_prdiv0)), // PRDIV0 PLL External Reference Divider


//! Control Register 6 	 MCG_C6_LOLIE0($(mcg_c6_lolie0)) | // LOLIE0 Loss of Lock interrupt Enable 	 MCG_C6_CME0($(mcg_c6_cme0)) | // CME0 Clock Monitor Enable 	 MCG_C6_VDIV0($(mcg_c6_vdiv0)), // VDIV0 PLL VCO Divider


//! Status and Control Register 	 MCG_SC_FCRDIV($(mcg_sc_fcrdiv)), // FCRDIV Internal Clock Reference Divider


//! Control Register 7 	 MCG_C7_OSCSEL($(mcg_c7_oscsel)), // OSCSEL MCG OSC Clock Select


