devicePage.template:
	//! Default value for PIT->SC register
 	static constexpr uint32_t pit_ldval = $(pit_ldval);


	//! PIT operation in debug mode
 	static constexpr uint32_t mcr = 	 PIT_MCR_FRZ($(pit_mcr_frz)) | // Freeze in debug mode 	 PIT_MCR_MDIS(0); // Disable


	/** 	 * Get clock frequency 	 * 	 * @return Input clock frequency as a uint32_t in Hz 	 */ 	static __attribute__((always_inline)) uint32_t getClockFrequency() { 	 return SystemBusClock; 	}
