devicePage.template:
	//! Frequency of RTC External Clock or Crystal
 	static constexpr uint32_t rtcclk_clock = $(rtcclk_clock)UL;


	//! Oscillator control register
 	static constexpr uint32_t cr = 	 RTC_CR_OSCE($(rtc_cr_osce)) | // Enables 32kHz oscillator [RTC_32K] 	 RTC_CR_CLKO($(rtc_cr_clko)) | // Disables RTC 32kHz Clock Output 	 RTC_CR_UM($(rtc_cr_um)) | // Update Mode 	 RTC_CR_SUP($(rtc_cr_sup)) | // Supervisor access 	 RTC_CR_WPE($(rtc_cr_wpe)) | // Wakeup Pin Enable 	 RTC_CR_SCP($(rtc_cr_scp)); // RTC Oscillator load capacitance


	/** 	 * Get RTC clock (internal, not masked by RTC_CR_CLKO) 	 * 	 * @return Clock frequency as uint32_t 	 */ 	static uint32_t getInternalClock() { 	 return (rtc->CR&RTC_CR_OSCE_MASK)?rtcclk_clock:0; 	}


	/** 	 * Get RTC clock (external, masked by RTC_CR_CLKO) 	 * 	 * @return Clock frequency as uint32_t 	 */ 	static uint32_t getExternalClock() { 	 return (rtc->CR&RTC_CR_CLKO_MASK)?0:getInternalClock(); 	}
