devicePage.template:
#ifndef SMC_STOPCTRL_PSTOPO #define SMC_STOPCTRL_PSTOPO(x) 0 #endif


#ifndef SMC_STOPCTRL_LPOPO #define SMC_STOPCTRL_LPOPO(x) 0 #endif


	// VLLS Control Register 	static constexpr uint8_t stopctrl = 	 SMC_STOPCTRL_PSTOPO($(smc_stopctrl_pstopo:0)) | // Partial Stop Option (if present) 	 SMC_STOPCTRL_PORPO($(smc_stopctrl_porpo:0)) | // POR Power Option 	 SMC_STOPCTRL_LPOPO($(smc_stopctrl_lpopo:0)) | // POR Power Option (if present) 	 SMC_STOPCTRL_LLSM($(smc_stopctrl_llsm:0)); // LLS or VLLS Mode Control
